configure.commands:
test -d SERVICE_ROOT/html && rm -rf SERVICE_ROOT/html
test ! -L SERVICE_ROOT/html && ln -s SERVICE_PREFIX/verknowsys /Software/Nginx/html

printf "
worker_processes 8;
pid SERVICE_PREFIX/service.pid;
error_log SERVICE_PREFIX/service.log;
events {
    worker_connections 1024;
}

http {
    include SERVICE_ROOT/conf/mime.types;
    default_type application/octet-stream;
    sendfile on;
    gzip on;
    gzip_disable "msie6";
    gzip_vary on;
    gzip_proxied any;
    gzip_comp_level 1;
    gzip_buffers 16 8k;
    gzip_http_version 1.1;
    gzip_types text/plain text/css application/json application/x-javascript text/xml application/xml application/xml+rss text/javascript;

    keepalive_timeout 270;
    server_names_hash_bucket_size 64;

    fastcgi_buffers 8 16k;
    fastcgi_buffer_size 32k;

    access_log off;

    server {
        listen $(cat SERVICE_PREFIX/.ports/0);
        server_name SERVICE_DOMAIN;
        server_name_in_redirect off;
        location / {
            root SERVICE_PREFIX/verknowsys;
            index index.html;
        }
    }

    server {
        listen $(cat SERVICE_PREFIX/.ports/0);
        server_name *.SERVICE_DOMAIN;
        server_name_in_redirect off;
        location / {
            root SERVICE_PREFIX/verknowsys;
            index error.html;
        }
    }

    include /home/*/SoftwareData/*/proxy.conf;
    include /Users/*/SoftwareData/*/proxy.conf;
}
" > SERVICE_PREFIX/service.conf

mv SERVICE_PREFIX/verknowsys SERVICE_PREFIX/verknowsys.$(date +%d%m%Y-%H%M%S)
export REL_ROOT_DIR=/Users/verknowsys/SoftwareData/Verknowsys
cp -vR ${REL_ROOT_DIR}/releases/$(cat ${REL_ROOT_DIR}/service.latest.release)/ SERVICE_PREFIX/verknowsys >> SERVICE_PREFIX/service.log 2>&1
