validate.commands:
test ! -f SERVICE_PREFIX/service.conf && printf "
worker_processes 2;
events {
    worker_connections 1024;
}

http {
    include SERVICE_ROOT/conf/mime.types;
    default_type application/octet-stream;
    sendfile on;
    keepalive_timeout 270;
    error_log SERVICE_PREFIX/service.log;
    server {
        listen SERVICE_ADDRESS:$(cat SERVICE_PREFIX/.ports/0);
        server_name SERVICE_DOMAIN SERVICE_ADDRESS;
        location / {
            root SERVICE_PREFIX/html;
            index index.html index.htm;
        }
    }
}
" > SERVICE_PREFIX/service.conf
SERVICE_ROOT/exports/nginx -t -c SERVICE_PREFIX/service.conf && printf 'Ngins started
' >> SERVICE_PREFIX/service.log 2>&1
